css:
[data-zen="true"] atom-pane-container atom-pane .item-views {
  background: #455463 !important;
}
[data-zen="true"] atom-pane-container atom-pane {
  border: none !important;
}
[data-zen="true"] atom-text-editor:not(.mini) {
  margin: 0 auto;
  max-width: 100%;
}
[data-zen="true"] atom-workspace {
  padding: 0;
  border: none !important;
}
[data-zen="true"] atom-workspace atom-panel-container.left:empty {
  padding: 0;
}
[data-zen="true"] .tab-bar {
  display: none;
}
[data-zen="true"] .status-bar {
  height: 0;
}
[data-zen="true"] ::shadow .wrap-guide {
  visibility: hidden;
}
[data-zen="true"] ::shadow .editor--private .vertical-scrollbar {
  opacity: 0;
  transition: opacity .2s .1s;
}
[data-zen="true"] ::shadow .editor--private:hover .vertical-scrollbar {
  opacity: 1;
}
[data-zen="true"][data-zen-word-count="visible"] .status-bar .word-count {
  position: fixed;
  bottom: 1rem;
  right: 1rem;
  padding: 0 1rem;
  background: #455463;
  color: #ced5db;
}
[data-zen="true"][data-zen-word-count-position="left"] .status-bar .word-count {
  right: auto;
  left: 1rem;
}
[data-zen="true"][data-zen-gutter="false"] ::shadow .gutter {
  display: none;
}
[data-zen="true"][data-zen-tabs="multiple"] .tab-bar,
[data-zen="true"][data-zen-tabs="single"] .tab-bar {
  display: flex;
  justify-content: center;
}
[data-zen="true"][data-zen-tabs="single"] .tab-bar .tab {
  display: none;
}
[data-zen="true"][data-zen-tabs="single"] .tab-bar .active {
  display: block;
}
